mapper.select:
select  from t_sys_identity

	and id =#{id,jdbcType=BIGINT}
	and name like "%"#{name,jdbcType=VARCHAR}"%"
	and remark =#{remark,jdbcType=VARCHAR}
	and acquiesce =#{acquiesce,jdbcType=TINYINT}
	and app_id =#{appId,jdbcType=TINYINT}


	ORDER BY ${orderBy}


	${order}

select ,group_concat(d.role_name) as roleNames
from t_sys_identity a
LEFT JOIN t_sys_identity_rel_role c on a.id = c.identity_id
LEFT JOIN t_sys_role d on c.role_id = d.id

	and a.id =#{id,jdbcType=BIGINT}
	and a.name like "%"#{name,jdbcType=VARCHAR}"%"
	and a.acquiesce =#{acquiesce,jdbcType=TINYINT}
	and a.app_id =#{appId,jdbcType=TINYINT}

GROUP BY  a.id,a.name,a.remark,a.app_id

	HAVING roleNames  like "%"#{roleNames}"%"


	ORDER BY ${orderBy}


	${order}

select  from t_sys_identity


	    and  app_id=#{appId}


		and  acquiesce=#{acquiesce}

select  from t_sys_identity

	and id =#{id,jdbcType=BIGINT}
	and name like "%"#{name,jdbcType=VARCHAR}"%"
	and remark =#{remark,jdbcType=VARCHAR}
	and acquiesce =#{acquiesce,jdbcType=TINYINT}
	and app_id =#{appId,jdbcType=TINYINT}

mapper.update:
update t_sys_identity


		name =#{name,jdbcType=VARCHAR},


		remark =#{remark,jdbcType=VARCHAR},


		acquiesce =#{acquiesce,jdbcType=TINYINT},


		app_id =#{appId,jdbcType=TINYINT}


where  id = #{id,jdbcType=BIGINT}

mapper.select:
select

from
	t_sys_identity
where
	app_id=#{appId}
and acquiesce = #{acquiesce}
