mapper.select:
select  from t_sys_role

	and id =#{id,jdbcType=BIGINT}
	and role_name like "%"#{roleName,jdbcType=VARCHAR}"%"
	and role_code like "%"#{roleCode,jdbcType=VARCHAR}"%"
	and already_set =#{alreadySet,jdbcType=INTEGER}
	and app_id =#{appId,jdbcType=TINYINT}
	and create_user_id =#{createUserId,jdbcType=BIGINT}
	and update_user_id =#{updateUserId,jdbcType=BIGINT}


	ORDER BY ${orderBy}


	${order}
